INSERT INTO MissingPersons (ID, DATE, NAME, PICTURE, DESCRIPTION) VALUES('6a8dc9bf-b6ea-4ceb-a0f5-cfab11362b47', '2006-11-07', N'JANE BALACDAO', NULL, N'Η JANE BALACDAO από τις Φιλιππίνες ημερομηνία γέννησης 2/07/1973, οικιακή βοηθός λείπει από το ξενοδοχείο LORDOS στο δρόμο Λάρνακας - Δεκέλειας, όπου διέμενε με την εργοδότρια από το Λίβανο, επισκέπτρια στην Κύπρο από τις 28.08.2006. Οποιαδήποτε πληροφορία να διαβιβαστεί στο ΤΑΕ Λάρνακας ή το ΚΕΜ Αρχηγείου Αστυνομίας.');
